class Solution:
    def isSubsequence(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: bool
        """
        # whether s in t as subseq
        st = 0
        for x in s :
            idx = t[st: ].find(x)
            if idx == -1:
                return False
            st = st + idx + 1
        return True

if __name__ == "__main__":
    s = input(' s = ')
    t = input(' t =  ')
    #t = ''
    so = Solution()
    print(so.isSubsequence(s, t))
